import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Main.css';

const Registration = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
            username: '',
            email: '',
            password: '',
            confirmPassword: ''
        });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if(FormData.password === FormData.confirmPassword && FormData.username && FormData.email) {
            navigate('/home');
        }
    };

    return (
        <div className="registration-container">
            <div className="registration-box">
                <div className="registration-title">Регистрация</div>
                <form onSubmit={handleSubmit} className="registration-input-container">
                    <label className="registration-label" htmlFor="username">Логин</label>
                    <input 
                        type="text"
                        id="username"
                        name="username"
                        className="login-input"
                        placeholder="Введите логин"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </form>
            </div>
        </div>
    );
};

export default Registration;
