import { useNavigate } from "react-router-dom"
import { useState } from "react"
import './Main.css'
import './CatalogPage.css'
import Header from "./Header"



function CatalogPage () {
    const statuses = ['Продолжается', 'Заморожен', 'Закончен', 'Брошен']
    const [activeStatuses, setActiveStatuses] = useState([])

    const navigate = useNavigate();
    const [search, setSearch] = useState('')

    //Оценка
    const [fromRate, setFromRate] = useState(null)
    const [beforeRate, setBeforeRate] = useState(null)

    const [fromChapters, setFromChapters] = useState(null)
    const [beforeChapters, setBeforeChapters] = useState(null)

    const handleChange = async (e) =>
    {
        switch (e.target.name)
        {
            case 'search':
                setSearch(e.target.value)
                break
            case 'fromRate':
                if(e.target.value >=0 && e.target.value <= 10)
                {
                    setFromRate(e.target.value)
                }
                break
            case 'beforeRate':
                if(e.target.value >=0 && e.target.value <= 10)
                {
                    setBeforeRate(e.target.value)
                }
                break
            case 'fromChapters':
                if(e.target.value >=0)
                {
                    setFromChapters(e.target.value)
                }
                break
            case 'beforeChapters':
                if(e.target.value >=0)
                {
                    setBeforeChapters(e.target.value)
                }
                break
        }
    }

    const handleActiveStatus = (e) =>
    {
        console.log('вызов')
        if(activeStatuses.includes(e.target.key))
        {
            let indx = activeStatuses.indexOf(e.target.value)
            activeStatuses.splice(indx)
            setActiveStatuses(activeStatuses - e.target.key)
        }
        else
        {
            activeStatuses.push(e.target.key)
            setActiveStatuses(activeStatuses + e.target.key)
        }
    }



    return (
        <div className="vertical-column-container">
            <Header></Header>
            <div className="center-setter">
                <div className="borderless-vertical-box">
                    <div className='general-margin'>
                        <div className='catalog-horizontal-box'>
                            <div className='catalog-results-area'>
                                <div className='catalog-results-area-header'>
                                    <div className="catalog-header">Каталог</div>
                                    <select className='catalog-select-sort'>
                                        <option value='by-popularity'>По популярности</option>
                                        <option value='by-views'>По просмотрам</option>
                                        <option value='by-rating'>По рейтингу</option>
                                        <option value='by-chapters'>По количеству глав</option>
                                    </select>
                                </div>
                                <input
                                className="catalog-search-input"
                                value={search}
                                type='text'
                                autoComplete="off"
                                onChange={handleChange}
                                placeholder="Поиск по названию"
                                name='search'/>
                                <div className="catalog-results-box">
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                    <img alt='Новелла' style={{width: `150px`, height:`200px`, display: `flex`, background: `#a1a1a1`}}/>
                                </div>
                            </div>
                            <div className='catalog-parameters-area'>
                                <div className='catalog-parameters-item'>
                                    <div className='catalog-parameter-header'>По оценке</div>
                                    <div className='catalog-parameter-row-box'>
                                        <span>От</span>
                                        <input className="catalog-parameter-input"
                                        type='number'
                                        value={fromRate}
                                        onChange={handleChange}
                                        name='fromRate'/>
                                        <span>До</span>
                                        <input className="catalog-parameter-input"
                                        type='number'
                                        value={beforeRate}
                                        onChange={handleChange}
                                        name='beforeRate'/>
                                    </div>
                                </div>
                                <div className='catalog-parameters-item'>
                                    <div className='catalog-parameter-header'>По количеству глав</div>
                                        <div className='catalog-parameter-row-box'>
                                            <span>От</span>
                                            <input className="catalog-parameter-input"
                                            type='number'
                                            value={fromChapters}
                                            onChange={handleChange}
                                            name='fromChapters'/>
                                            <span>До</span>
                                            <input className="catalog-parameter-input"
                                            type='number'
                                            value={beforeChapters}
                                            onChange={handleChange}
                                            name='beforeChapters'/>
                                        </div>
                                </div>
                                <div className='catalog-parameters-item'>
                                    <div className='catalog-parameter-header'>По статусу</div>
                                    <div className="catalog-parameter-status-box">
                                        {statuses.map(status => (
                                            
                                                <button 
                                                className={activeStatuses.includes(status) ? 'catalog-status-button-active' : 'catalog-status-button'} 
                                                onClick={handleActiveStatus}
                                                key={status}>{status}</button>
                                            )
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CatalogPage
