import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.username && formData.password) {
      navigate('/home');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-title">Авторизация</div>
        <form onSubmit={handleSubmit} className="login-input-container">
          <label className="login-label" htmlFor="username">Логин</label>
          <input
            type="text"
            id="username"
            name="username"
            className="login-input"
            placeholder="Введите логин"
            value={formData.username}
            onChange={handleChange}
            required
          />
          <label className="login-label" htmlFor="password">Пароль</label>
          <input
            type="password"
            id="password"
            name="password"
            className="login-input"
            placeholder="Введите пароль"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <div className="login-links">
            <button onClick={() => navigate('/forgot-password')} className="login-link">Восстановить пароль</button>
            <button onClick={() => navigate('/registration')} className="login-link">Зарегистрироваться</button>
          </div>
          <button type="submit" className="login-button">
            Использовать
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
