import React from "react";
import './Main.css';

const BestAuthor = () => {
    return (
        <div className='borderless-box' style={{minWidth: 300, minHeight: 210}}>
            {/* Заголовок "Автор" */}
            <div style={{ color: '#8ECA82', fontSize: 22, marginBottom: 4, marginLeft: 2 }}>
                Автор
            </div>
            {/* Блок с фото и описанием автора */}
            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'flex-start', marginBottom: 18 }}>
                <div className='book-container' style={{ marginRight: 16, background: '#888', width: 75, height: 100 }} />
                <div>
                    <div style={{ color: '#e05a43', fontSize: 20, fontWeight: 500 }}>Автор</div>
                    <div style={{ color: '#e05a43', fontSize: 14, marginTop: 2 }}>Описание автора</div>
                </div>
            </div>
            {/* Заголовок "Его лучшая работа" */}
            <div style={{ color: '#8ECA82', fontSize: 20, marginBottom: 4, marginLeft: 2 }}>
                Его лучшая работа
            </div>
            {/* Блок с работой */}
            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'flex-start' }}>
                <div className='book-container' style={{ marginRight: 12, background: '#888', width: 50, height: 65 }} />
                <div>
                    <div style={{ color: '#e05a43', fontSize: 16, fontWeight: 500 }}>Название новеллы</div>
                    <div style={{ color: '#e05a43', fontSize: 12, marginTop: 2 }}>Автор (страна)</div>
                    <div style={{ color: '#e05a43', fontSize: 12, marginTop: 2 }}>Основные жанры</div>
                </div>
            </div>
        </div>
    );
}

export default BestAuthor;
