import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import './ChapterPage.css';
import './Main.css';


function ChapterPage() {
    const { chapterId } = useParams();

    const [title, setTitle] = useState(null);
    const [chapter, setChapter] = useState(null);
    const [paragraphs, setParagraphs] = useState([]);

    useEffect(() => {
        if(chapterId) {
            fetch(`http://localhost:5004/api/chapters/${chapterId}`)
                .then(response => response.json())
                .then(data => {
                    setChapter(data);
                    setParagraphs(data.content.split(/\r?\n/).filter(p => p.trim() !== ""));
                });
        }
    }, [chapterId]);

    if (!chapter) {
        return <div>Loading...</div>;
    }

    return (
        <div className="vertical-column-container">
            <div className='navigation-bar'>
                <div className='navigation-bar-button'>Назад</div>
                <div className='navigation-bar-title-box'>
                    <div className='navigation-bar-title-box-title'>Название тайтла</div>
                    <div className='navigation-bar-title-box-chapter'>Глава</div>
                </div>
                <div className='navigation-bar-button'>Вперед</div>
            </div>
            <button className='chapter-page-settings'>Настройки</button>
            <div className="chapter-center-setter">
                <div className='chapter-title'>{chapter.name ? chapter.name : 'Название главы отсутствует'}</div>
                <div className='chapter-content'>
                    <div className='chapter-content-text'>
                        {paragraphs.map((paragraph, index) => (
                            <p key={index} className='chapter-content-text-paragraph'>{paragraph}</p>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ChapterPage;
