import {React, useState, useEffect} from 'react'
import { useAuthContext } from '../../providers/ThemeProvider/AuthProvider'



export const LoginWindow = (props) =>
{
    const [userlogin, setLogin] = useState('')
    const [password, setPassword] = useState('')
    const {login, isAuthenticated} = useAuthContext()

    const handleInputChange = async (e) =>
    {
        switch (e.target.name)
        {
            case 'login':
                setLogin(e.target.value)
                break
            case 'password':
                setPassword(e.target.value)
                break
        }
    }

    const handleChangeWindow = (window) =>
    {
        props.handleSetChoicedWindow(window)
    }

    const handleLogin = (inputlogin, inputpassword) =>
    {
        login(inputlogin, inputpassword)

        if(!isAuthenticated)
        {
            console.error("Ошибка авторизации")
            return;
        }

        console.log("Успех")
    }

    return(
        <div>
            <div className='window-header'>Авторизация</div>
            <div className='input-box'>
                <div className='input-header'>Логин или Email</div>
                <input className='input'
                name='login'
                type='text'
                placeholder='Введите логин или почту'
                value={userlogin}
                onChange={handleInputChange}/>
            </div>
            <div className='input-box'>
                <div className='input-header'>Пароль</div>
                <input className='input'
                name='password'
                type='text'
                placeholder='Введите пароль'
                value={password}
                onChange={handleInputChange}/>
            </div>
            <div className='href-section'>
                <button className='href-button' onClick={() => {handleChangeWindow('Authorizating')}}>Регистрация</button>
                <button className='href-button' onClick={() => {handleChangeWindow('RecoverPassword')}}>Восстановить пароль</button>
            </div>
            <button className='auth-button'
            onClick={() => {
                handleLogin(userlogin, password)
            }}>Войти</button>
        </div>
    )
}
