import React, { useState } from 'react';
import '../Main.css';
import './ProfileWindow.css';
import { useNavigate } from 'react-router-dom';
import FumoJpg from '../icons/fumo.jpg';
import {ReactComponent as BellOnIcon} from '../icons/bell_on.svg';

const ProfileWindow = () => {
    const navigate = useNavigate();
    const [isOpen, setIsOpen] = useState(false);

    const handleOpen = () => setIsOpen(prev => !prev);
    const handleClose = () => setIsOpen(false);

    return (
        <div style={{display: 'inline-block'}}>
            <button onClick={handleOpen} className='profile-button'>
                <img src={FumoJpg} alt='profile' className='profile-avatar'/>
            </button>
            {isOpen && (
                <div className='profile-window'>
                    <div className='profile-window-header'>
                        <div>Мой профиль</div>
                        <div>Название профиля</div>
                    </div> 
                    <div className='profile-window-body'>
                        <button className='profile-link'>
                            <BellOnIcon className='profile-icon'/>
                            <div>Уведомления</div>
                        </button>
                        <button className='profile-link'>
                            <BellOnIcon className='profile-icon'/>
                            <div>Уведомления</div>
                        </button>
                        <button className='profile-link'>
                            <BellOnIcon className='profile-icon'/>
                            <div>Уведомления</div>
                        </button>
                        <button className='profile-link'>
                            <BellOnIcon className='profile-icon'/>
                            <div>Уведомления</div>
                        </button>
                        <button className='profile-link'>
                            <BellOnIcon className='profile-icon'/>
                            <div>Уведомления</div>
                        </button>
                        <button className='profile-link' onClick={handleClose}>Выход</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ProfileWindow;
