import { useEffect, useState } from "react";
import '../DialogWindows/SearchWindow.css';

function Novella({ id}) {
  const [novella, setNovella] = useState(null);

  useEffect(() => {
    fetch(`/api/titles/${id}`)
      .then(res => res.json())
      .then(data => setNovella(data));
  }, [Name]);

  if (!novella) return <div>Загрузка...</div>;

  return (
    <div className='search-window-inresult-column'>
        <div className='search-window-inresult-title'>{novella.Name}</div>
        <div className='search-window-inresult-subtitle'>
        </div>
    </div>
  );
}

export default Novella;
