import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import './Main.css';
import Header from './Header.js';



function TitlePage() {
  const { id } = useParams();
  const [title, setTitle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5004/api/titles/${id}`)
      .then(res => res.json())
      .then(data => setTitle(data));
  }, [id]);

  if (!title) return <div>Загрузка...</div>;

  return (
    <div className='vertical-column-container'>
        <Header />
        <div className='center-setter'>
          <div className='borderless-vertical-box'>
            <div className='general-margin'>
              <div className='title-page-background-img'>
              </div>
              <h1>23235</h1>
            </div>
          </div>
        </div>
    </div>
  );
}

export default TitlePage;
