import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Main.css';

const NewPassword = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        username: ''
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if(formData.email && formData.username) {
            navigate('/home');
        }
    };

    return (
        <div className="container">
            <div className="box">
                <div className="title">Восстановление пароля</div>
                <form onSubmit={handleSubmit} className="input-container">
                    <label className="label" htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        className="input"
                        placeholder="Введите email" 
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                    <label className="label" htmlFor="username">Логин</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        className="input"
                        placeholder="Введите логин"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                    <div className="horizontal-container">
                        <button className="link" onClick={() => navigate('/login')}>Авторизироваться</button>
                        <button className="link" onClick={() => navigate('/registration')}>Зарегистрироваться</button>
                    </div>
                    <button type="submit" className="button">Отправить подтверждение</button>
                </form>
            </div>
        </div>
    )
}
