import {React, useState, useEffect, useRef} from 'react';
import { useNavigate } from 'react-router-dom';
import './SearchWindow.css';
import '../Main.css';

const searchMock = [
    {type: 'Тайтлы', title: 'Название', author: 'Автор', chapters: 'Главы', avatar: 'аватар'},
    {type: 'Пользователи', nickname: 'Никнейм', avatar: 'аватар'},
    {type: 'Люди', nickname: 'Никнейм', avatar: 'аватар'},
]

const SearchWindow = () => {
    const ref = useRef();
    const categories = ['Тайтлы', 'Пользователи', 'Люди'];

    const [search, setSearch] = useState('');
    const [isOpen, setIsOpen] = useState(false);
    const [searchResults, setSearchResults] = useState(searchMock);
    const [activeCategory, setActiveCategory] = useState('Тайтлы');
    const [formData, setFormData] = useState({
        search: ''
    });

    const handleOpen = () => setIsOpen(true);
    const handleClose = () => {
        setIsOpen(false)
        handleChange({target: {name: 'search', value: ''}})
    };

    const filtered = searchResults.filter(result => result.type === activeCategory && (
        (activeCategory === 'Тайтлы' && result.title.toLowerCase().includes(search.toLowerCase())) ||
        (activeCategory === 'Пользователи' && result.username.toLowerCase().includes(search.toLowerCase())) ||
        (activeCategory === 'Люди' && result.name.toLowerCase().includes(search.toLowerCase()))
    ));

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSearch(value);
        setIsOpen(value.length > 0);
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (ref.current && !ref.current.contains(event.target)) {
                handleClose();
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);
  

    return (
        <div ref={ref} style={{display: 'inline-block', position: 'relative'}}>
            <div className='search-window-input'>
                <input 
                type="text" 
                name="search" 
                value={formData.search} 
                onChange={handleChange} 
                placeholder='Поиск...' 
                onClick={handleOpen}
                autoComplete='off'
                />
            </div>
            {isOpen && (
                <div>
                    <div className="overlay" onClick={handleClose}></div>
                    <div className="search-window">
                        <div className="search-window-column">
                            <div className='search-window-categories'>
                                {categories.map(cat => (
                                    <button
                                        key={cat}
                                        className={activeCategory === cat ? 'active' : ''}
                                        onClick={() => setActiveCategory(cat)}
                                    >
                                        {cat}
                                    </button>
                                ))}
                            </div>
                            <div className='search-window-results-column'>
                                {filtered.map(result => (
                                    <div key={result.id} className='search-window-result'>
                                        {result.type === 'Тайтлы' && <>
                                            <div className='search-window-result-title'>{result.title}</div>
                                            <div className='search-window-result-author'>{result.author}</div>
                                        </>}
                                        {result.type === 'Пользователи' && <>
                                            <div className='search-window-result-title'>{result.username}</div>
                                            <div className='search-window-result-author'>{result.email}</div>
                                        </>}
                                        {result.type === 'Люди' && <>
                                            <div className='search-window-result-title'>{result.name}</div>
                                            <div className='search-window-result-author'>{result.surname}</div>
                                        </>}
                                    </div>
                                ))}
                                {filtered.length === 0 && (
                                    <div className='search-window-result-empty'>
                                        <span className='search-window-result-empty-text'>Расширенный поиск находится в </span>
                                        <span className='search-window-link'>каталоге</span>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    )
}

export default SearchWindow;
