import { useEffect } from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import './NovellaCard.js'


function NovellaCard ()
{
    const {id, coverId, authorId, name} = useParams()

    const [author, setAuthor] = useState(null)
    const [cover, setCover] = useState(null)

    useEffect(() =>
    {
        if(coverId && authorId)
        {
            fetch(`http://api/authors/${authorId}`)
            .then(response => response.json())
            .then(data => setAuthor(data))
            fetch(`http://api/image/${coverId}`)
            .then(response => response.json())
            .then(data => setCover(data))
        }
    }, [coverId, authorId])

    if(coverId == null && authorId == null) return <div>Загрузка</div>

    return (
        <div className='novella-card-body'>
            <div className='novella-card-cover-container'>
                <img src={cover.path} alt="Обложка"></img>
            </div>
            <div className='novella-card-title-header'>
                <div className='novella-card-title'>name</div>
                <div className='novella-card-author'>{author.nickname ? author.nickname : "Загрузка"}</div>
            </div>
        </div>
    )
}

export default NovellaCard
